# -!- coding: utf-8 -!-import my_oneforallimport my_csvimport scanimport osprint(f"""[INFO]                                    主程序[INFO]                                     LELE[INFO]                                     LELE[CHOOSE] 【1】OneForAll --> Xray   //输入一个主域名，程序自动采集子域名，采集完成后，自动导入Xray扫描[CHOOSE] 【2】Xray                //输入txt路径后，xray对其扫描[CHOOSE] 【3】OneForAll          //只采集子域名，采集完成后显示结果[NOTICE]                       //Oneforall 的扫描结果存储在其目录下----------------------------------------------------------------------------------------""")print(os.getcwd()+"\domain.txt")choose=int(input("[INPUT] 输入选择："))if choose==3:    csv=my_oneforall.sh("python .\OneForAll\oneforall.py --target {0} run".format(input('[INPUT] 请输入主域名：')))[1]    my_csv.load_csv(csv)elif choose==2:        scan.scan("mr")elif choose==1:    my_oneforall.sh("python .\OneForAll\oneforall.py --target {0} run".format(input('[INPUT] 请输入主域名：')))    scan.scan(os.getcwd()+"\domain.txt")